	public void doHelp(String helpItem) {
		if (helpItem != null)
			try {
				if (browser1 != null) {
					browser1.setCurrentLocation(helpItem+".html");
				}
			} catch (Exception e) {e.printStackTrace();}

		show();
	}
	
